import * as WebBrowser from 'expo-web-browser';
import * as React from 'react';
import { LineChart, BarChart, PieChart, ProgressChart } from 'react-native-chart-kit'
import { Ionicons } from '@expo/vector-icons';
import { Image, Platform, StyleSheet, Text, TouchableOpacity, View, Dimensions } from 'react-native';
import { Collapse, CollapseHeader, CollapseBody} from 'accordion-collapse-react-native'
import { RectButton, ScrollView } from 'react-native-gesture-handler';
// import Axios from 'axios';

const styles = StyleSheet.create({
    pageTitle:{
        textAlign: 'center'
    },
    barChartTitle: {

    }
})
const screenHeight = Dimensions.get("window").height;

function OptionButton({ icon, label, isLastOption }) {
    return (
      <RectButton style={[styles.option, isLastOption && styles.lastOption]}>
        <View style={{ flexDirection: 'row' }}>
          <View style={styles.optionIconContainer}>
            <Ionicons name={icon} size={22} color="rgba(0,0,0,0.35)" />
          </View>
          <View style={styles.optionTextContainer}>
            <Text style={styles.optionText}>{label}</Text>
          </View>
        </View>
      </RectButton>
    );
  }

export default function dailyStats() {
    const [states, setStates] = React.useState([])
    const [cases, setCases] = React.useState([])
    const [tested, setTested] = React.useState([])
    const [deaths, setDeaths] = React.useState([])
    React.useEffect( () => {
        async function fetchData() {
            const res = await fetch("https://covidtracking.com/api/states")
            res.json()
            .then(res => {
            let statesArray = []
            let casesArray = []
            let testedArray = []
            let deathsArray = []
            res.forEach(info => {
                statesArray.push(info.state)
                casesArray.push(info.positive)
                testedArray.push(info.positive + info.negative)
                deathsArray.push(info.death)
            })
            setStates(statesArray)
            setCases(casesArray)
            setTested(testedArray)
            setDeaths(deathsArray)
        })
        .catch(err => console.log(err))
        }
    fetchData();

    }) 
    const chartConfig = {
        backgroundGradientFrom: `#ffffff`,
        backgroundGradientFromOpacity: .5,
        backgroundGradientTo: `#000000`,
        backgroundGradientToOpacity: 0.5,
        fillShadowGradient: `rgb(0, 0, 0)`,
        color: (opacity = 1) => `rgba(55, 22, 120, ${opacity})`,
        strokeWidth: 2, // optional, default 3
        barPercentage: .8,
    }
    return (
        <View>
            <Collapse>
                <CollapseHeader>
                <OptionButton icon="md-medkit" />
                </CollapseHeader>
                <CollapseBody>
                    <BarChart
                    data={{
                        labels: states,
                        datasets: [
                            {
                                data: tested
                            }
                        ]
                    }}
                    width={2500}
                    height={screenHeight / 4}
                    chartConfig={chartConfig}
                    />
                </CollapseBody>
            </Collapse>
            <Collapse>
                <CollapseHeader>
                <View>
                <Text>Cases by State</Text>
                </View>
                </CollapseHeader>
                <CollapseBody>
                    <BarChart
                    data={{
                        labels: states,
                        datasets: [
                            {
                                data: cases
                            }
                        ]
                    }}
                    width={2500}
                    height={screenHeight / 4}
                    chartConfig={chartConfig}
                    />
                </CollapseBody>
            </Collapse>
            <Collapse>
                <CollapseHeader>
                <View>
                <Text>Deaths by State</Text>
                </View>
                </CollapseHeader>
                <CollapseBody>
                    <BarChart
                    data={{
                        labels: states,
                        datasets: [
                            {
                                data: deaths
                            }
                        ]
                    }}
                    width={2500}
                    height={screenHeight / 4}
                    chartConfig={chartConfig}
                    />
                </CollapseBody>
            </Collapse>
        </View>
    )
}
