import * as WebBrowser from 'expo-web-browser';
import * as React from 'react';
import { LineChart, BarChart, PieChart, ProgressChart } from 'react-native-chart-kit'
import { Image, Platform, StyleSheet, Text, TouchableOpacity, View, Dimensions } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
// import Axios from 'axios';

export default function dailyStats() {
    const [states, setStates] = React.useState(["AK", "AL", "AZ"])
    const [loading, setLoading] = React.useState(true)
    const [cases, setCases] = React.useState(["0", "0", "0"])
    React.useEffect( () => {
        async function fetchData() {
            const res = await fetch("https://covidtracking.com/api/states")
            res.json()
            .then(res => {
            let statesArray = []
            let casesArray = []
            res.forEach(info => {
                statesArray.push(info.state)
                casesArray.push(info.positive)
            })
            setStates(statesArray)
            setCases(casesArray) 
            setLoading(false) 
        })
        .catch(err => console.log(err))
        }
    fetchData();

    })
    const testData = {
        labels: states,
        datasets: [
            {
                data: cases
            }
        ]
    } 
    const chartConfig = {
        backgroundGradientFrom: `#000000`,
        backgroundGradientFromOpacity: 1,
        backgroundGradientTo: `#000000`,
        backgroundGradientToOpacity: 0.5,
        fillShadowGradient: `rgb(0, 0, 0)`,
        color: (opacity = 1) => `rgba(0, 0, 0, ${opacity})`,
        strokeWidth: 2, // optional, default 3
        barPercentage: .8,
    }
    return (
        <View>
        <Text>U.S. Coronavirus Cases by State</Text>
        <ScrollView horizontal={true}>
            {!loading && <BarChart
                data={testData}
                width={2500}
                height={300}
                chartConfig={chartConfig}
            /> }
        </ScrollView>
        </View>
    )
}
