import * as WebBrowser from 'expo-web-browser';
import * as React from 'react';
import { LineChart, BarChart, PieChart, ProgressChart, StackedBarChart } from 'react-native-chart-kit'
import { Ionicons } from '@expo/vector-icons';
import { Image, Platform, StyleSheet, Text, ListItem, TouchableOpacity, View, Dimensions } from 'react-native';
import { Collapse, CollapseHeader, CollapseBody} from 'accordion-collapse-react-native'
import { RectButton, ScrollView } from 'react-native-gesture-handler';

const screenHeight = Dimensions.get("window").height;
const screenWidth = Dimensions.get("window").width;
const styles = StyleSheet.create({
    pageTitle:{
        textAlign: 'center'
    },
    container: {
      flex: 1,
      backgroundColor: '#0C2340',
    },
    contentContainer: {
      paddingTop: 15,
      backgroundColor: '#0C2340'
    },
    optionIconContainer: {
      marginRight: 12,
    },
    option: {
      backgroundColor: '#ffffff',
      paddingHorizontal: 15,
      paddingVertical: 15,
      borderWidth: StyleSheet.hairlineWidth,
      borderBottomWidth: 0,
      borderColor: '#0C2340',
      flexDirection: 'row'
    },
    lastOption: {
      borderBottomWidth: StyleSheet.hairlineWidth,
    },
    optionText: {
      fontSize: 15,
      alignSelf: 'flex-start',
      marginTop: 1,
    },
  });
function OptionButton({ icon, label }) {
    return (
        <View style={styles.option}>
          <View style={styles.optionIconContainer}>
            <Ionicons name={icon} size={22} color="rgba(0,0,0, 1)" />
          </View>
          <View style={styles.optionTextContainer}>
            <Text style={styles.optionText}>{label}</Text>
          </View>
        </View>
    );
  }

export default function dailyStats() {
    const [states, setStates] = React.useState([])
    const [cases, setCases] = React.useState([])
    const [tested, setTested] = React.useState([])
    const [deaths, setDeaths] = React.useState([])
    const [hospitalized, setHospitalized] = React.useState([])
    React.useEffect( () => {
        async function fetchData() {
            const res = await fetch("https://covidtracking.com/api/states")
            res.json().then(res => {
            let statesArray = []
            let casesArray = []
            let testedArray = []
            let deathsArray = []
            let hospitalizedArray = []
            res.forEach(info => {
                statesArray.push(info.state)
                casesArray.push(info.positive)
                testedArray.push(info.totalTestResults)
                deathsArray.push(info.death)
                hospitalizedArray.push(info.hospitalizedCumulative)
            })
            setStates(statesArray)
            setCases(casesArray)
            setTested(testedArray)
            setDeaths(deathsArray)
            setHospitalized(hospitalizedArray)
        })
        .catch(err => console.log(err))
        }
    fetchData();  
    }, []) 
    const colors = ["FFFFFF", "000000", "333333", "666666", "999999", "CCCCCC", "CCCC99", "9999CC", "666699", "660000", "663300", "996633", "003300", "003333", "003399", "000066", "330066", "660066", "990000", "993300", "CC9900", "006600", "336666", "0033FF", "000099", "660099", "990066", "CC0000", "CC3300", "FFCC00", "009900", "006666", "0066FF", "0000FF", "9900CC", "FF0099", "CC3333", "FF6600", "FFFF33", "00FF00", "00CCCC", "00CCFF", "3366FF", "9933FF", "FF00FF", "FF6666", "FF6633", "FFFF66", "66FF66", "66CCCC", "00FFFF", "3399FF", "9966FF", "FF66FF", "FF9999", "FF9966", "FFFF99", "99FF99", "66FFCC", "99FFFF", "66CCFF", "9999FF", "FF99FF"]
    let testedChartData = []
    let casesChartData = []
    let stackedBarChartData = []
    let row = []
    const barChartConfig = {
        backgroundGradientFrom: `#0C2340`,
        backgroundGradientFromOpacity: 1,
        backgroundGradientTo: `#000000`,
        backgroundGradientToOpacity: 0,
        fillShadowGradient: `rgb(255, 255, 255)`,
        color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
        strokeWidth: 2, // optional, default 3
        barPercentage: 0.8,
    }
    for (let i = 0; i < states.length; i++) {
        testedChartData.push(
            {
                name: states[i],
                data: tested[i],
                color: `#${colors[i]}`,
                legendFontColor: `#ffffff`,
                legendFontSize: 10

            }
        )
        casesChartData.push(
            {
                name: states[i],
                data: cases[i],
                color: `#${colors[i]}`,
                legendFontColor: `#ffffff`,
                legendFontSize: 10
            }
        )
        row = [states[i], cases[i], hospitalized[i], deaths[i]]
        stackedBarChartData.push(row)
    }

    const compare = (a,b) => {
        if (a[1] < b[1]) return 1;
        if (a[1] > b[1]) return -1;
        return 0;
    }
    const compareObject = (a, b) => {
        if (a.data < b.data) return 1;
        if (a.data > b.data) return -1;
        return 0;
    }
    stackedBarChartData.sort(compare);
    testedChartData = testedChartData.sort(compareObject).slice(0, 10)
    return (
        <ScrollView style={styles.container}>
            <Collapse>
                <CollapseHeader>
                    <OptionButton style={styles.option} icon="md-medkit" label="Tests by State and Territory" />
                </CollapseHeader>
                <CollapseBody>
                <ScrollView horizontal={true}>
                    <BarChart data={{labels: states, datasets: [{ data: tested }]}} width={1900} height={screenHeight / 4} chartConfig={barChartConfig} decimalPlaces={0} fromZero={true} />
                    </ScrollView>
                    <PieChart width={screenWidth} height={300} data={testedChartData} chartConfig={barChartConfig} accessor="data" bgColor="none" paddingLeft="25" hasLegend={true} />
                </CollapseBody>
            </Collapse>
            <Collapse>
                <CollapseHeader>
                    <OptionButton style={styles.option} icon="md-sad" label="Cases by State and Territory" />
                </CollapseHeader>
                <CollapseBody>
                    <ScrollView horizontal={true}>
                        <BarChart data={{ labels: states, datasets: [{ data: cases }] }} width={1900} height={screenHeight / 4} chartConfig={barChartConfig} decimalPlaces={0} fromZero={true} />
                        <PieChart width={screenWidth} height={300} data={casesChartData} chartConfig={barChartConfig} accessor="data"
                        bgColor="none"
                        paddingLeft="35"
                        hasLegend={true}
                        />
                        </ScrollView>
                </CollapseBody>
            </Collapse>
            <Collapse>
                <CollapseHeader>
                    <OptionButton style={styles.option} icon="md-warning" label="Deaths by State and Territory" />
                </CollapseHeader>
                <CollapseBody>
                    <ScrollView horizontal={true}>
                        <BarChart data={{ labels: states, datasets: [{ data: deaths }]}} width={1900} height={screenHeight / 4} decimalPlaces={0} chartConfig={barChartConfig} />
                        <PieChart width={screenWidth} height={300} data={testedChartData} chartConfig={barChartConfig} accessor="data" bgColor="none" paddingLeft="25" hasLegend={true} />
                    </ScrollView>
                </CollapseBody>
            </Collapse>
            <Collapse>
                <CollapseHeader>
                    <OptionButton style={styles.option} icon="md-warning" label="Combined Stats by State and Territory" />
                </CollapseHeader>
                <CollapseBody>
                    <ScrollView horizontal={true} style={{paddingLeft: 15}}>
                        <StackedBarChart
                        data={{
                            labels: stackedBarChartData.map(data => data[0]),
                            legend: ["Cases", "Hospitalized", "Deaths"],
                            data: stackedBarChartData.map(data => data.slice(1, 4)),
                            barColors: ["#00DD44", "#FFFF00", "#000000"]
                        }}
                        width={2900}
                        height={screenHeight / 2}
                        decimalPlaces={0}
                        chartConfig={barChartConfig}
                        />
                    </ScrollView>
                </CollapseBody>
            </Collapse>
        </ScrollView>
    )
}
